import React, { createContext } from 'react';
import B from './B';
import C from './C';

// First step 
export const MyContext = createContext(null);

function App() {
  const [x,setX]= useState(0)
  const [y,setY]=useState(0)
  return (
    //second Step, Wrap your whole appöiction in context.provider 
    <MyContext.Provider value={{x,y,setX,setY}}>
    <div> 
      <B></B>
      <C></C>
    </div>
    </MyContext.Provider>
  );
}

export default App;
