import React, { useContext } from 'react';
import { MyContext } from './App'


export default function B(){
    const values = useContext(MyContext)
    return(
        <div>
            <h1> B, X= {values.x}<button onClick={()=>values.setY(values.y%1)}>increment X</button></h1>
        </div>
    )
}
